import { useContext } from 'react';
import defaultTheme from './defaultTheme.js';
import { ThemeContext } from '../../components/ThemeProvider/ThemeProvider.js';

function styleHandler(target) {
  let {
    theme: {
      [target]: __styles
    }
  } = useContext(ThemeContext);
  if (!__styles) __styles = defaultTheme.button; // console.log(__styles)
  // __styles.replace(/\s+/g, ' ')
  // console.log('before string', __styles)
  // __styles = JSON.stringify(JSON.parse(__styles))

  __styles = JSON.stringify(__styles).replace(/\\n/g, '').replace(/\s\s+/g, ' '); // console.log('string', __styles)

  __styles = JSON.parse(__styles); // console.log('string', __styles)

  return __styles;
}

export { styleHandler as default };
